\begin{array}{rl} & u^{\mathrm{st}(0)} = \pmb{p}^{\mathrm{st}(0)}\cdot \pmb {u} = u^{\mathrm{st}},\\ & u^{\mathrm{st}(1)} = \pmb{p}^{\mathrm{st}(1)}\cdot \pmb {u} = 0,\\ & u^{\mathrm{st}(2)} = \pmb{p}^{\mathrm{st}(2)}\cdot \pmb {u} = (\pmb {u}\cdot \pmb {1}_z)(\pmb {\delta}\circ \pmb {\epsilon}\cdot \pmb{p}^*),\\ & u^{\mathrm{st}(3)} = \pmb{p}^{\mathrm{st}(3)}\cdot \pmb {u} = (\pmb{M}^{(2)}\pmb{p}^{\mathrm{st}(1)} + \pmb{M}^{(1)}\pmb{p}^{\mathrm{st}(2)})\cdot \pmb {u} = (\pmb {u}\cdot \pmb {1}_z)(\pmb {\delta}\circ \pmb {\epsilon}\cdot \pmb{p}^{\mathrm{st}(1)})\\ & \qquad = (\pmb {u}\cdot \pmb {1}_z)\{(\pmb {\delta}\cdot \pmb{p}^*)(\pmb {\delta}\circ \pmb {\epsilon}\circ \pmb{y}^*\cdot \pmb {1}_x) + (\pmb {\epsilon}\cdot \pmb{p}^*)(\pmb {\delta}\circ \pmb {\epsilon}\circ \pmb{x}^*\cdot \pmb {1}_y)\} ,\\ & u^{\mathrm{st}(4)} = \pmb{p}^{\mathrm{st}(4)}\cdot \pmb {u} = (\pmb{M}^{(2)}\pmb{p}^{\mathrm{st}(2)} + \pmb{M}^{(1)}\pmb{p}^{\mathrm{st}(3)})\cdot \pmb {u} = (\pmb {u}\cdot \pmb {1}_z)(\pmb {\delta}\circ \pmb {\epsilon}\cdot \pmb{p}^{\mathrm{st}(2)})\\ & \qquad = (\pmb {u}\cdot \pmb {1}_z)[(\pmb {\delta}\circ \pmb {\epsilon}\cdot \pmb{p}^*)(\pmb {\delta}\circ \pmb {\epsilon}\cdot \pmb {1}_z)\\ & \qquad +\{(\pmb {\delta}\cdot \pmb{p}^*)(\pmb {\delta}\circ \pmb{y}^*\cdot \pmb {1}_x) + (\pmb {\epsilon}\cdot \pmb{p}^*)(\pmb {\delta}\circ \pmb{x}^*\cdot \pmb {1}_y)\} (\pmb {\delta}\circ \pmb {\epsilon}\circ \pmb{y}^*\cdot \pmb {1}_x)\\ & \qquad +\{(\pmb {\delta}\cdot \pmb{p}^*)(\pmb {\epsilon}\circ \pmb{y}^*\cdot \pmb {1}_x) + (\pmb {\epsilon}\cdot \pmb{p}^*)(\pmb {\epsilon}\circ \pmb{x}^*\cdot \pmb {1}_y)\} (\pmb {\delta}\circ \pmb {\epsilon}\circ \pmb{x}^*\cdot \pmb {1}_y)]. \end{array} \tag{A44}
